import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom'; // Import useNavigate to handle navigation
import { FaMapMarkerAlt, FaCalendarAlt, FaChevronRight } from 'react-icons/fa';
import { GiNails } from 'react-icons/gi';
import './css/Services.css';

const Services = () => {
    const location = useLocation();
    const navigate = useNavigate(); // Use useNavigate for navigation
    const { selectedServices = [], totalPayment = 0 } = location.state || {}; // Get the state passed via navigation

    const [showTechnicianSelection, setShowTechnicianSelection] = useState(false); // State to show or hide the Nail Technician section
    const [selectedTechnician, setSelectedTechnician] = useState(null); // State to store the selected technician

    useEffect(() => {
        // Show the Nail Technician section if services are selected
        if (selectedServices.length > 0) {
            setShowTechnicianSelection(true);
        }
    }, [selectedServices]);

    const handleViewAllServices = () => {
        navigate('/all-services', { state: { selectedServices } }); // Navigate to all-services and pass selected services
    };

    const handleTechnicianSelect = (technician) => {
        setSelectedTechnician(technician);
    };

    return (
        <div className="booking-container">
            <h1 className="booking-header">Booking Appointment</h1>

            {/* Step 1: Select Salon */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">1</div>
                    <h2>Salon</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <FaMapMarkerAlt className="mr-2 text-pink-500" />
                        <span>292 Stone Rd W Unit 7A, Guelph, ON N1G 3C4, Canada</span>
                    </div>
                </div>
            </div>

            {/* Step 2: Choose Service */}
            <div className="step-container" onClick={handleViewAllServices}>
                <div className="step-title">
                    <div className="step-number">2</div>
                    <h2>Choose Service</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <GiNails className="mr-2" />
                        {selectedServices.length === 0 ? (
                            <span>View All Services</span>
                        ) : (
                            <span>Selected {selectedServices.length} Services</span>
                        )}
                    </div>
                    <FaChevronRight />
                </div>
            </div>

            {/* Show Selected Services */}
            {selectedServices.length > 0 && (
                <div className="selected-services-list">
                    {selectedServices.map((service, index) => (
                        <div key={index} className="selected-service-item">
                            {service.name}
                        </div>
                    ))}
                    <div className="total-payment">
                        Total Payment: ${totalPayment.toFixed(2)}
                    </div>
                </div>
            )}

            {/* Step 3: Choose Date, Time & Stylist */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">3</div>
                    <h2>Choose Date, Time & Stylist</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <FaCalendarAlt className="mr-2" />
                        <span>Today, Sat (20/08)</span>
                    </div>
                    <button className="step-content-button bg-red-600 text-white">Weekend</button>
                </div>
            </div>

            {/* Nail Technician Selection */}
            {showTechnicianSelection && (
                <div className="step-container">
                    <div className="step-title">
                        <div className="step-number">4</div>
                        <h2>Nail Technician</h2>
                    </div>
                    <div className="technician-selection">
                        <div
                            className={`technician-option ${selectedTechnician === 'Technician 1' ? 'selected' : ''}`}
                            onClick={() => handleTechnicianSelect('Technician 1')}
                        >
                            Technician 1
                        </div>
                        <div
                            className={`technician-option ${selectedTechnician === 'Technician 2' ? 'selected' : ''}`}
                            onClick={() => handleTechnicianSelect('Technician 2')}
                        >
                            Technician 2
                        </div>
                        <div
                            className={`technician-option ${selectedTechnician === 'Technician 3' ? 'selected' : ''}`}
                            onClick={() => handleTechnicianSelect('Technician 3')}
                        >
                            Technician 3
                        </div>
                    </div>
                </div>
            )}

            {/* Confirm Button */}
            <button className="confirm-button">Confirm Booking</button>
            <p className="note-text">You can cancel or reschedule without any fee.</p>
        </div>
    );
};

export default Services;
