import React from 'react';
import CustomCarousel from './CustomCarousel';
import '../CustomCarousel.css';

const Home = () => {
    return (
        <div className="container mx-auto py-4">
            <CustomCarousel />
            <section className="my-12 mx-auto max-w-7xl px-6">
                <h2 className="text-4xl font-bold mb-4 text-center">Why Clients Choose Us</h2>

                <div className="flex flex-wrap justify-between items-start mt-8">
                    <div className="w-full md:w-1/2 pr-8">
                        <p className="text-lg leading-relaxed mb-6">
                            Our salon is staffed with highly skilled and experienced nail technicians who are passionate about delivering top-notch service. Whether you're looking for a classic manicure, gel nails, or intricate nail art, our technicians have the expertise to bring your vision to life.
                        </p>
                        <p className="text-lg leading-relaxed mb-6">
                            At 24 De Nails, your health and safety are our top priorities. We adhere to strict cleanliness and sanitation protocols, ensuring that all tools and equipment are thoroughly sterilized between each client. You can relax and enjoy your nail service knowing that you're in a clean and hygienic environment.
                        </p>
                        <p className="text-lg leading-relaxed">
                            Convenience is key, and that's why we offer online booking through Dash Booking, allowing you to schedule your appointments with ease and flexibility. Say goodbye to waiting on hold or trying to coordinate your busy schedule - just book online, and we'll take care of the rest.
                        </p>
                    </div>

                    <div className="w-full md:w-1/2 mt-8 md:mt-0">
                        <img
                            src="/images/image6.png" 
                            alt="Salon"
                            className="rounded-lg shadow-lg object-cover w-full h-full"
                            style={{ width: '500px', height: '400px' }}
                        />
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Home;
