import React, { useState, useEffect } from 'react';
import { FaMapMarkerAlt, FaCalendarAlt, FaChevronRight, FaChevronDown, FaUser } from 'react-icons/fa';
import { GiNails } from 'react-icons/gi';
import './css/Services.css';

const Services = () => {
    const [selectedServices, setSelectedServices] = useState([]);
    const [showTechnicianSelection, setShowTechnicianSelection] = useState(false);
    const [selectedTechnician, setSelectedTechnician] = useState(null);
    const [showDatePicker, setShowDatePicker] = useState(false);
    const [dates, setDates] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);

    useEffect(() => {
        // Tạo danh sách 7 ngày tiếp theo (bao gồm cả hôm nay) theo múi giờ Toronto
        const today = new Date();
        const nextSevenDays = [];
        for (let i = 0; i < 7; i++) {
            const date = new Date(today);
            date.setDate(today.getDate() + i);
            nextSevenDays.push(new Date(date.toLocaleString("en-US", { timeZone: "America/Toronto" }))); // Chuyển đổi sang múi giờ Toronto
        }
        setDates(nextSevenDays);
        setSelectedDate(nextSevenDays[0]); // Chọn hôm nay mặc định
    }, []);

    const handleTechnicianToggle = () => {
        setShowTechnicianSelection(!showTechnicianSelection);
    };

    const handleTechnicianSelect = (technician) => {
        setSelectedTechnician(technician);
        setShowTechnicianSelection(false);
    };

    const handleDateToggle = () => {
        setShowDatePicker(!showDatePicker);
    };

    const handleDateSelect = (date) => {
        setSelectedDate(date);
        setShowDatePicker(false);
    };

    const technicians = [
        { id: 1, name: "Wendy", image: '/images/people.png' },
        { id: 2, name: "Trung", image: '/images/people.png' },
        { id: 3, name: "Kelly", image: '/images/people.png' },
        { id: 4, name: "Ken", image: '/images/people.png' },
        { id: 5, name: "Tina", image: '/images/people.png' }
    ];

    return (
        <div className="booking-container">
            <h1 className="booking-header">Booking Appointment</h1>

            {/* Step 1: Select Salon */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">1</div>
                    <h2>Salon</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <FaMapMarkerAlt className="mr-2 text-pink-500" />
                        <span>292 Stone Rd W Unit 7A, Guelph, ON N1G 3C4, Canada</span>
                    </div>
                </div>
            </div>

            {/* Step 2: Choose Service */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">2</div>
                    <h2>Choose Service</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <GiNails className="mr-2" />
                        {selectedServices.length === 0 ? (
                            <span>View All Services</span>
                        ) : (
                            <span>Selected {selectedServices.length} Services</span>
                        )}
                    </div>
                    <FaChevronRight />
                </div>
            </div>

            {/* Step 3: Choose Date, Time & Stylist */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">3</div>
                    <h2>Choose Date, Time & Stylist</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <FaCalendarAlt className="mr-2" />
                        <span>Today, Sat (20/08)</span>
                    </div>
                    <button className="step-content-button bg-red-600 text-white">Weekend</button>
                </div>

                {/* Nail Technician Selection */}
                <div className="technician-dropdown">
                    <div className="flex items-center cursor-pointer" onClick={handleTechnicianToggle}>
                        <FaUser className="mr-2" />
                        <span>{selectedTechnician ? selectedTechnician.name : "Choose Nail Technician"}</span>
                        {/* Đổi hướng của mũi tên dựa trên trạng thái showTechnicianSelection */}
                        <FaChevronDown className={`ml-2 transition-transform ${showTechnicianSelection ? 'rotate-180' : 'rotate-90'}`} />
                    </div>

                    {showTechnicianSelection && (
                        <div className="technician-carousel">
                            <div className="technician-options">
                                {technicians.map((technician) => (
                                    <div
                                        key={technician.id}
                                        className={`technician-option ${selectedTechnician?.id === technician.id ? 'selected' : ''}`}
                                        onClick={() => handleTechnicianSelect(technician)}
                                    >
                                        <img src={technician.image} alt={technician.name} className="technician-image" />
                                        <span>{technician.name}</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            </div>

            {/* Confirm Button */}
            <button className="confirm-button">Confirm Booking</button>
            <p className="note-text">You can cancel or reschedule without any fee.</p>
        </div>
    );
};

export default Services;
