import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";

const images = [
    '/images/image1.png',
    '/images/image2.png',
    '/images/image3.png',
    '/images/image4.png',
    '/images/image5.png',
    '/images/image6.png'
];

const CustomCarousel = () => {
    return (
        <Carousel
            showArrows={true}
            showThumbs={false}
            showStatus={false}
            infiniteLoop={true}
            autoPlay={true}
            interval={3000}
            transitionTime={500}
            centerMode={true} // Kích hoạt chế độ hiển thị ở giữa
            centerSlidePercentage={33.33} // Chiếm 33.33% mỗi hình ảnh, vừa đủ để 3 hình xuất hiện cùng lúc
        >
            {images.map((src, index) => (
                <div key={index}>
                    <img src={src} alt={`Slide ${index + 1}`} />
                </div>
            ))}
        </Carousel>
    );
};

export default CustomCarousel;
