import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { scroller } from 'react-scroll';
import 'bootstrap/dist/css/bootstrap.min.css';
import './css/Navbar.css';

const Navbar = () => {
    const navigate = useNavigate();

    const handleAboutClick = (e) => {
        e.preventDefault();
        navigate('/'); // Điều hướng về Home trước
        setTimeout(() => {
            scroller.scrollTo('why-clients-choose-us', {
                duration: 400, // Giảm thời gian để cuộn nhanh hơn
                delay: 0,
                smooth: 'easeInOutQuart',
                offset: -80, // Điều chỉnh để cuộn chính xác đến đầu của mục (trừ đi chiều cao của navbar)
            });
        }, 50); // Thời gian chờ để đảm bảo trang Home được render trước khi cuộn
    };

    return (
        <nav className="bg-gray-800 p-4 fixed top-0 left-0 w-full z-50">
            <div className="container mx-auto flex justify-between items-center">
                <h1 className="text-4xl font-bold text-white">LT Nails Guelph</h1>
                <div className="space-x-8">
                    <Link to="/" className="hover:text-gray-400 text-white">Home</Link>
                    <button
                        onClick={handleAboutClick}
                        className="hover:text-gray-400 text-white"
                        style={{ background: 'none', border: 'none', cursor: 'pointer' }}
                    >
                        About
                    </button>
                    <Link to="/services" className="hover:text-gray-400 text-white">Services</Link>
                    <Link to="/gallery" className="hover:text-gray-400 text-white">Gallery</Link>
                    <Link to="/contact" className="hover:text-gray-400 text-white">Contacts</Link>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
