import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './css/AllServices.css';

const api = axios.create({
    baseURL: 'http://localhost:5118/api',
});

const AllServices = () => {
    const [servicesData, setServicesData] = useState({});
    const [selectedCategory, setSelectedCategory] = useState("");
    const [selectedServices, setSelectedServices] = useState([]);
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        // Gọi API để lấy danh sách dịch vụ
        const fetchServices = async () => {
            try {
                const response = await api.get('/services');
                const servicesByCategory = response.data.reduce((acc, service) => {
                    if (!acc[service.category]) {
                        acc[service.category] = [];
                    }
                    acc[service.category].push(service);
                    return acc;
                }, {});
                setServicesData(servicesByCategory);
                setSelectedCategory(Object.keys(servicesByCategory)[0]);
            } catch (error) {
                console.error('Error fetching services:', error);
            }
        };

        fetchServices();
    }, []);

    useEffect(() => {
        if (location.state && location.state.selectedServices) {
            setSelectedServices(location.state.selectedServices);
        }
    }, [location.state]);

    const handleServiceChange = (service, checked) => {
        if (checked) {
            setSelectedServices([...selectedServices, service]);
        } else {
            setSelectedServices(selectedServices.filter((s) => s.name !== service.name));
        }
    };

    const isServiceSelected = (service) => {
        return selectedServices.some((s) => s.name === service.name);
    };

    const handleFinished = () => {
        if (selectedServices.length > 0) {
            const totalPayment = selectedServices.reduce((total, service) => total + parseFloat(service.price.replace('$', '')), 0);
            navigate('/services', { state: { selectedServices, totalPayment } });
        }
    };

    const totalPayment = selectedServices.reduce((total, service) => total + parseFloat(service.price.replace('$', '')), 0);

    return (
        <div className="all-services-page">
            <div className="services-container">
                <div className="services-sidebar">
                    <h2 className="services-heading">SERVICES</h2>
                    {Object.keys(servicesData).map((category) => (
                        <button
                            key={category}
                            className={`category-button ${category === selectedCategory ? "active" : ""}`}
                            onClick={() => setSelectedCategory(category)}
                        >
                            {category}
                        </button>
                    ))}
                </div>

                <div className="services-list">
                    <h2>{selectedCategory}</h2>
                    <ul>
                        {servicesData[selectedCategory]?.map((service, index) => (
                            <li key={index} className="service-item">
                                <div className="service-details">
                                    <div className="service-header">
                                        <span className="service-name">{service.name}</span>
                                        <span className="service-price">${service.price}</span>
                                    </div>
                                    <div className="service-info">
                                        <span>{service.duration || ""}</span>
                                        <span>Fixed</span>
                                    </div>
                                    <p className="service-description">{service.description}</p>
                                </div>
                                <input
                                    type="checkbox"
                                    className="service-checkbox"
                                    checked={isServiceSelected(service)}
                                    onChange={(e) => handleServiceChange(service, e.target.checked)}
                                />
                            </li>
                        ))}
                    </ul>
                </div>
            </div>

            <div className="fixed-footer">
                <div className="footer-selected">
                    <span className="footer-link">Selected {selectedServices.length} Services</span>
                </div>
                <div className="footer-total-payment">
                    <span>Total Payment: ${totalPayment.toFixed(2)}</span>
                </div>
                <button
                    className={`footer-button ${selectedServices.length === 0 ? "disabled" : "enabled"}`}
                    disabled={selectedServices.length === 0}
                    onClick={handleFinished}
                >
                    Finished
                </button>
            </div>
        </div>
    );
};

export default AllServices;
