import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Login.css';
import { FaGoogle } from 'react-icons/fa'; // Import icon Google

const Login = ({ setIsAuthenticated, setUsername }) => {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    // Hàm để làm mới token
    const refreshToken = async () => {
        try {
            const refreshToken = localStorage.getItem('refreshToken');
            const response = await fetch('http://localhost:5118/api/Auth/refresh-token', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ refreshToken }),

            });

            if (response.ok) {
                const data = await response.json();
                localStorage.setItem('token', data.accessToken);
                localStorage.setItem('refreshToken', data.refreshToken);
                return data.accessToken;
            } else {
                console.error('Failed to refresh token');
                // Nếu không làm mới được token, có thể điều hướng người dùng đến trang đăng nhập
                setIsAuthenticated(false);
                navigate('/login');
            }
        } catch (error) {
            console.error('Error during token refresh:', error);
        }
    };

    // Hàm để lấy thông tin người dùng
    let refreshTokenRetries = 0;

    const fetchUserInfo = async (token) => {
        if (refreshTokenRetries >= 2) {
            alert('Session expired. Please log in again.');
            setIsAuthenticated(false);
            navigate('/login');
            return;
        }

        try {
            const response = await fetch('http://localhost:5118/api/Auth/userinfo', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.status === 401) { // Token hết hạn
                refreshTokenRetries++;
                const newAccessToken = await refreshToken();
                return fetchUserInfo(newAccessToken); // Gọi lại sau khi làm mới token
            } else if (response.ok) {
                return await response.json();
            } else {
                alert('Failed to fetch user info');
            }
        } catch (error) {
            console.error('Error during fetching user info:', error);
        }
    };


    // Hàm xử lý đăng nhập
    const handleLogin = async (e) => {
        e.preventDefault();

        const loginData = {
            phoneNumber,
            password,
        };

        try {
            const response = await fetch('http://localhost:5118/api/Auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(loginData),
            });

            if (response.ok) {
                const loginData = await response.json();
                localStorage.setItem('token', loginData.token);
                localStorage.setItem('refreshToken', loginData.refreshToken);

                // Gọi hàm fetchUserInfo với token
                const userInfo = await fetchUserInfo(loginData.token);
                if (userInfo) {
                    localStorage.setItem('username', userInfo.userName);
                    setUsername(userInfo.userName);
                    setIsAuthenticated(true);
                    navigate('/'); // Chuyển hướng sau khi đăng nhập thành công
                }
            } else {
                alert('Invalid credentials');
            }
        } catch (error) {
            console.error('Error during login:', error);
            alert('An error occurred during login. Please try again.');
        }
    };

    const handleGoogleLogin = () => {
        // Thêm logic đăng nhập bằng Google
        alert('Google login functionality here');
    };

    const handleRegister = () => {
        navigate('/register'); // Chuyển hướng tới trang đăng ký
    };

    return (
        <div className="login-page">
            <div className="login-container">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <label>Phone Number:</label>
                        <input
                            type="text"
                            value={phoneNumber}
                            onChange={(e) => setPhoneNumber(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="login-button">Login</button>
                </form>
                <div className="separator">or</div>
                <button className="google-button" onClick={handleGoogleLogin}>
                    <FaGoogle className="google-icon" /> Sign in with Google
                </button>
                <div className="login-register-container">
                    <p>Don't have an account?</p>
                    <button className="register-button" onClick={handleRegister}>Register</button>
                </div>
            </div>
        </div>
    );
};

export default Login;
