import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './css/BookingHistory.css';

const BookingHistory = () => {
    const [bookings, setBookings] = useState([]);

    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const userName = localStorage.getItem('username');
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:5118/api/Bookings/user/${userName}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setBookings(response.data);
            } catch (error) {
                console.error("Error fetching booking history:", error);
                alert("Không thể tải lịch sử đặt lịch.");
            }
        };

        fetchBookings();
    }, []);

    return (
        <div className="booking-history">
            <h2 className="booking-history-title">Lịch Sử Đặt Lịch</h2>
            <ul className="booking-list">
                {bookings.map((booking, index) => (
                    <li key={index} className="booking-card">
                        <p><strong>Kỹ thuật viên:</strong> {booking.technicianName}</p>
                        <p><strong>Dịch vụ:</strong> {booking.serviceName}</p>
                        <p><strong>Thời gian:</strong> {new Date(booking.startTime).toLocaleString()}</p>
                        <p><strong>Thời lượng:</strong> {booking.duration} phút</p>                  
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BookingHistory;
