import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import './css/AllServices.css';

const servicesData = {
    "ARTIFICIAL NAILS": [
        { name: "ACRYLIC FULL SET", price: "50", duration: "1 hr 15 min", description: "Basic acrylic nail application." },
        { name: "ACRYLIC REFILL", price: "40", duration: "1 hr 15 min", description: "Refill and reshaping of existing acrylic nails." },
        { name: "SOLAR FULL SET", price: "55", duration: "1 hr 15 min", description: "Solar nails full set application." },
        { name: "SOLAR REFILL", price: "45", duration: "1 hr 15 min", description: "Refill and reshaping of existing solar nails." },
        { name: "BIO GEL FULL SET", price: "60", duration: "1 hr 15 min", description: "Full set application using bio gel." },
        { name: "BIO GEL REFILL", price: "50", duration: "1 hr 15 min", description: "Refill and reshaping of existing bio gel nails." },
        { name: "POWDER COLOR FULL SET", price: "55", duration: "1 hr 15 min", description: "Full set application with colored powder." },
        { name: "POWDER COLOR REFILL", price: "45", duration: "1 hr 15 min", description: "Refill and reshaping of existing colored powder nails." },
        { name: "FULL SET DIP", price: "50", duration: "1 hr 15 min", description: "Dip powder nail full set application." }
    ],
    "NAIL CARE": [
        { name: "MANICURE", price: "20", duration: "30 min", description: "Standard manicure with shaping and polish." },
        { name: "MANICURE SHELLAC", price: "35", duration: "45 min", description: "Shellac manicure for long-lasting polish." },
        { name: "MANICURE DIP", price: "45", duration: "45 min", description: "Dip powder manicure for added strength." },
        { name: "PEDICURE", price: "30", duration: "30 min", description: "Standard pedicure service." },
        { name: "PEDICURE SHELLAC", price: "45", duration: "45 min", description: "Shellac pedicure for long-lasting polish." },
        { name: "PEDI & MANI REG", price: "50", duration: "1 hr", description: "Combination of regular pedicure and manicure." },
        { name: "PEDI REG & MANI SHELLAC", price: "65", duration: "1 hr 15 min", description: "Combination of regular pedicure and shellac manicure." },
        { name: "PEDI/MANI BOTH SHELLAC", price: "75", duration: "1 hr 15 min", description: "Combination of shellac pedicure and shellac manicure." }
    ],
    "WAXING": [
        { name: "EYEBROWS", price: "10", description: "Professional eyebrow shaping." },
        { name: "LIP", price: "5", description: "Upper lip waxing for a smooth finish." },
        { name: "CHIN", price: "7", description: "Chin waxing service." },
        { name: "FULL FACE", price: "35", description: "Complete face waxing service." },
        { name: "FULL ARM", price: "25", description: "Complete arm waxing service." },
        { name: "HALF ARM", price: "20", description: "Half arm waxing service." },
        { name: "UNDER ARM", price: "15", description: "Underarm hair removal." },
        { name: "BACK", price: "40", description: "Back waxing service." },
        { name: "BIKINI", price: "30", description: "Bikini area waxing service." },
        { name: "FULL LEG", price: "50", description: "Complete leg waxing service." },
        { name: "HALF LEG", price: "25/30", description: "Half leg waxing service." },
        { name: "EYEBROWS (WAX + TINTING)", price: "20", description: "Eyebrow waxing with tinting service." }
    ],
    "POLISH CHANGE": [
        { name: "HANDS", price: "10 - 25", duration: "15 min", description: "Quick polish change for hands." },
        { name: "TOES", price: "10 - 25", duration: "15 min", description: "Quick polish change for toes." },
        { name: "DESIGN", price: "5 & UP", duration: "15 min", description: "Nail design based on the design choice." },
        { name: "FRENCHI/OMBRE", price: "5 & UP", duration: "15 min", description: "French or ombre nail designs." },
        { name: "SHELLAC REMOVAL", price: "8", description: "Shellac nail polish removal." },
        { name: "FAKE NAIL REMOVAL", price: "15", description: "Fake nail removal service." },
        { name: "KIDS PEDI & MANI", price: "38", description: "Pedicure and manicure service for kids." }
    ]
};

const AllServices = () => {
    const navigate = useNavigate();
    const [selectedCategory, setSelectedCategory] = useState("ARTIFICIAL NAILS");
    const [selectedServices, setSelectedServices] = useState([]);

    const handleServiceChange = (service, checked) => {
        if (checked) {
            setSelectedServices([...selectedServices, service]);
        } else {
            setSelectedServices(selectedServices.filter((s) => s.name !== service.name));
        }
    };

    const totalPayment = selectedServices.reduce((total, service) => total + parseFloat(service.price), 0);

    const handleFinished = () => {
        if (selectedServices.length > 0) {
            navigate("/services");
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <h1>All Services</h1>
            <div>
                <h2>{selectedCategory}</h2>
                <ul>
                    <li>
                        <input type="checkbox" onChange={(e) => handleServiceChange({ name: "Test Service", price: 50 }, e.target.checked)} />
                        Test Service - $50
                    </li>
                </ul>
            </div>
            <div className="fixed-footer">
                <div className="footer-selected">
                    <span>Selected {selectedServices.length} Services</span>
                </div>
                <div className="footer-total">
                    <span>Total Payment: ${totalPayment.toFixed(2)}</span>
                </div>
                <button
                    className={selectedServices.length === 0 ? "disabled" : "enabled"}
                    disabled={selectedServices.length === 0}
                    onClick={handleFinished}
                >
                    Finished
                </button>
            </div>
        </div>
    );
};


export default AllServices;
