import React from 'react';
import { FaMapMarkerAlt, FaCalendarAlt, FaChevronRight } from 'react-icons/fa'; // Using FaMapMarkerAlt and FaChevronRight
import { GiNails } from 'react-icons/gi'; // Using the nail icon
import './css/Services.css';

const Services = () => {
    return (
        <div className="booking-container">
            <h1 className="booking-header">Booking Appointment</h1>

            {/* Step 1: Select Salon */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">1</div>
                    <h2>Salon</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <FaMapMarkerAlt className="mr-2 text-pink-500" /> {/* Location icon */}
                        <span>292 Stone Rd W Unit 7A, Guelph, ON N1G 3C4, Canada</span>
                    </div>
                </div>
            </div>

            {/* Step 2: Select Service */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">2</div>
                    <h2>Choose Service</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <GiNails className="mr-2" /> {/* Changed to nail icon */}
                        <span>View All Services</span>
                    </div>
                    <FaChevronRight /> {/* Changed to the chevron-right icon */}
                </div>
            </div>

            {/* Step 3: Select Date, Time & Stylist */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">3</div>
                    <h2>Choose Date, Time & Stylist</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <FaCalendarAlt className="mr-2" /> {/* Calendar icon remains unchanged */}
                        <span>Today, Sat (17/08)</span>
                    </div>
                    <button className="step-content-button bg-red-600 text-white">Weekend</button>
                </div>
            </div>

            {/* Confirm Button */}
            <button className="confirm-button">Confirm Booking</button>
            <p className="note-text">You can cancel or reschedule without any fee.</p>
        </div>
    );
};

export default Services;
