import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Login.css';
import { FaGoogle } from 'react-icons/fa'; // Import icon Google

const Login = () => {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = (e) => {
        e.preventDefault();

        // Giả lập việc đăng nhập thành công và lưu token vào localStorage
        if (phoneNumber === '1234567890' && password === 'password') {
            localStorage.setItem('token', 'dummy-token');
            localStorage.setItem('username', 'John Doe');
            navigate('/'); // Chuyển hướng sau khi đăng nhập thành công
        } else {
            alert('Invalid credentials');
        }
    };

    const handleGoogleLogin = () => {
        // Thêm logic đăng nhập bằng Google
        alert('Google login functionality here');
    };

    const handleRegister = () => {
        navigate('/register'); // Chuyển hướng tới trang đăng ký
    };

    return (
        <div className="login-page">
            <div className="login-container">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <label>Phone Number:</label>
                        <input
                            type="text"
                            value={phoneNumber}
                            onChange={(e) => setPhoneNumber(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="login-button">Login</button>
                </form>
                <div className="separator">or</div>
                <button className="google-button" onClick={handleGoogleLogin}>
                    <FaGoogle className="google-icon" /> Sign in with Google
                </button>
                <div className="login-register-container">
                    <p>Don't have an account?</p>
                    <button className="register-button" onClick={handleRegister}>Register</button>
                </div>
            </div>
        </div>
    );
};


export default Login;
