import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './css/BookingHistory.css';

const BookingHistory = () => {
    const [bookings, setBookings] = useState([]);

    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const userName = localStorage.getItem('username'); 
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:5118/api/Bookings/user/${userName}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setBookings(response.data);
            } catch (error) {
                console.error("Error fetching booking history:", error);
                alert("Không thể tải lịch sử đặt lịch.");
            }
        };

        fetchBookings();
    }, []);

    return (
        <div className="booking-history">
            <h2>Booking History</h2>
            {userBookings.map((booking, index) => (
                <div key={index} className="booking-card">
                    <h3>Dịch vụ:</h3>
                    <p className="booking-details"><span>Service:</span> {booking.serviceNames.join(', ')}</p>
                    <p className="booking-details"><span>Thời gian:</span> {new Date(booking.startTime).toLocaleString()}</p>
                    <p className="booking-details"><span>Thời lượng:</span> {booking.duration} phút</p>
                    <p className="booking-details"><span>Khách hàng:</span> {booking.customerName}</p>
                    <p className="booking-details"><span>Kỹ thuật viên:</span> {booking.technicianId}</p>
                </div>
            ))}
        </div>
    );
};

export default BookingHistory;
