import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { scroller } from 'react-scroll';
import 'bootstrap/dist/css/bootstrap.min.css';
import './css/Navbar.css';

const Navbar = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [username, setUsername] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        const storedUsername = localStorage.getItem('username');
        if (token) {
            setIsAuthenticated(true);
            setUsername(storedUsername || '');
        }
    }, []);

    const handleAboutClick = (e) => {
        e.preventDefault();
        navigate('/'); 
        setTimeout(() => {
            scroller.scrollTo('why-clients-choose-us', {
                duration: 400, // Giảm thời gian để cuộn nhanh hơn
                delay: 0,
                smooth: 'easeInOutQuart',
                offset: -80, // Điều chỉnh để cuộn chính xác đến đầu của mục (trừ đi chiều cao của navbar)
            });
        }, 50); // Thời gian chờ để đảm bảo trang Home được render trước khi cuộn
    };

    const handleContactsClick = (e) => {
        e.preventDefault();
        navigate('/'); 
        setTimeout(() => {
            scroller.scrollTo('contacts', {
                duration: 400, 
                delay: 0,
                smooth: 'easeInOutQuart',
                offset: -80, 
            });
        }, 50); 
    };

    const handleServicesClick = () => {
        if (isAuthenticated) {
            navigate('/services');
        } else {
            navigate('/login'); // Điều hướng đến trang đăng nhập nếu chưa đăng nhập
        }
    };

    return (
        <nav className="bg-gray-800 p-4 fixed top-0 left-0 w-full z-50">
            <div className="container mx-auto flex justify-between items-center">
                <h1 className="text-4xl font-bold text-white">LT Nails Guelph</h1>
                <div className="space-x-8">
                    <Link to="/" className="hover:text-gray-400 text-white">Home</Link>
                    <button
                        onClick={handleAboutClick}
                        className="hover:text-gray-400 text-white"
                        style={{ background: 'none', border: 'none', cursor: 'pointer' }}
                    >
                        About
                    </button>
                    <Link to="/services" className="hover:text-gray-400 text-white">Services</Link>
                    <Link to="/gallery" className="hover:text-gray-400 text-white">Gallery</Link>
                    <button
                        onClick={handleContactsClick}
                        className="hover:text-gray-400 text-white"
                        style={{ background: 'none', border: 'none', cursor: 'pointer' }}
                    >
                        Contacts
                    </button>
                    {isAuthenticated ? (
                        <div className="relative inline-block text-left">
                            <button className="hover:text-gray-400 text-white">
                                {username}
                            </button>
                            <div className="absolute right-0 mt-2 w-40 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none">
                                <div className="py-1">
                                    <Link
                                        to="/account"
                                        className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                                    >
                                        My Account
                                    </Link>
                                    <button
                                        onClick={handleLogout}
                                        className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                                    >
                                        Log Out
                                    </button>
                                </div>
                            </div>
                        </div>
                    ) : (
                        <Link to="/login" className="hover:text-gray-400 text-white">Sign In</Link>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
