import React from 'react';
import './css/AllServices.css';

const servicesData = {
    "ARTIFICIAL NAILS": [
        { name: "ACRYLIC FULL SET", price: "$50" },
        { name: "ACRYLIC REFILL", price: "$40" },
        // ... thêm dịch vụ khác
    ],
    "NAIL CARE": [
        { name: "MANICURE", price: "$20" },
        { name: "MANICURE SHELLAC", price: "$35" },
        // ... thêm dịch vụ khác
    ],
    "WAXING": [
        { name: "EYEBROWS", price: "$10" },
        { name: "LIP", price: "$5" },
        // ... thêm dịch vụ khác
    ],
    "POLISH CHANGE": [
        { name: "HANDS", price: "$10 - $25" },
        { name: "TOES", price: "$10 - $25" },
        // ... thêm dịch vụ khác
    ],
};

const AllServices = () => {
    const [selectedCategory, setSelectedCategory] = React.useState("ARTIFICIAL NAILS");

    return (
        <div className="all-services-page">
            <div className="header-image">
                <div className="salon-info">
                    <h1>24 De Nails</h1>
                    <p>218 Silvercreek Pkwy N, Guelph, ON N1H 8E8</p>
                    <p>(437) 602-6886</p>
                </div>
            </div>

            <div className="services-container">
                <div className="services-sidebar">
                    {Object.keys(servicesData).map((category) => (
                        <button
                            key={category}
                            className={`category-button ${category === selectedCategory ? "active" : ""}`}
                            onClick={() => setSelectedCategory(category)}
                        >
                            {category}
                        </button>
                    ))}
                </div>

                <div className="services-list">
                    <h2>{selectedCategory}</h2>
                    <ul>
                        {servicesData[selectedCategory].map((service, index) => (
                            <li key={index} className="service-item">
                                <span>{service.name}</span>
                                <span>{service.price}</span>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default AllServices;
