import React, { useState } from 'react';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';

const images = [
    '/images/image6.png',
    '/images/image6.png',
    '/images/image6.png',
    '/images/image6.png',
    '/images/image6.png',
    '/images/image6.png',
    '/images/image6.png',
    '/images/image6.png',
    '/images/image6.png'
];

const Gallery = () => {
    const [photoIndex, setPhotoIndex] = useState(0);
    const [isOpen, setIsOpen] = useState(false);

    return (
        <div className="my-12 mx-auto max-w-7xl px-6">
            <h2 className="text-4xl font-bold mb-4 text-center">Gallery</h2>
            <p className="text-center mb-8">
                Explore our complete collection of work, featuring manicures, pedicures, unique nail designs, custom art, and more. Every design showcased here has been carefully crafted by our talented nail technicians.
            </p>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {images.map((src, index) => (
                    <div
                        key={index}
                        className="relative group cursor-pointer"
                        onClick={() => {
                            setPhotoIndex(index);
                            setIsOpen(true);
                        }}
                    >
                        <img
                            src={src}
                            alt={`Gallery ${index + 1}`} // Loại bỏ các từ như "image", "photo", "picture"
                            className="object-cover w-full h-64 rounded-lg shadow-lg transition-transform transform group-hover:scale-105"
                        />
                    </div>
                ))}
            </div>

            {isOpen && (
                <Lightbox
                    mainSrc={images[photoIndex]}
                    nextSrc={images[(photoIndex + 1) % images.length]}
                    prevSrc={images[(photoIndex + images.length - 1) % images.length]}
                    onCloseRequest={() => setIsOpen(false)}
                    onMovePrevRequest={() =>
                        setPhotoIndex((photoIndex + images.length - 1) % images.length)
                    }
                    onMoveNextRequest={() =>
                        setPhotoIndex((photoIndex + 1) % images.length)
                    }
                />
            )}
        </div>
    );
};

export default Gallery;
