import React from 'react';
import CustomCarousel from './CustomCarousel';
import '../CustomCarousel.css';

const Home = () => {
    return (
        <div className="container mx-auto py-4">
            <CustomCarousel />
            <h1 className="text-4xl font-bold mb-8">Welcome to Mickey Nails</h1>
            <p className="mt-4">
                Our qualified team provides a full range of nail design services to satisfy even the most demanding clients. With our help, you can easily acquire a new look for your nails to stand out from the crowd.
            </p>

            {/* New Section */}
            <section className="my-12 mx-auto max-w-7xl px-6 text-center">
                <h2 className="text-3xl font-bold mb-4">What Makes Clients Prefer Us</h2>
                <p className="text-lg leading-relaxed mb-6">
                    Our salon boasts a team of talented and seasoned nail technicians who are passionate about providing outstanding services. Whether you want a timeless manicure, durable gel nails, or detailed nail art, our technicians are skilled at turning your ideas into reality.
                </p>
                <p className="text-lg leading-relaxed mb-6">
                    At LT Nails Guelph, your well-being is our utmost concern. We follow strict hygiene and sanitation guidelines, ensuring that every tool and piece of equipment is thoroughly disinfected between clients. You can unwind with the confidence that your service is being carried out in a clean and safe setting.
                </p>
                <p className="text-lg leading-relaxed">
                    To make things even more convenient, we offer online appointment booking through Dash Booking, giving you the flexibility to schedule at your convenience. No more lengthy phone calls or struggling to find a slot � just book online, and we�ll take care of the rest.
                </p>
            </section>
        </div>
    );
};

export default Home;
