import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom'; // Import useNavigate để điều hướng
import { FaMapMarkerAlt, FaCalendarAlt, FaChevronRight } from 'react-icons/fa';
import { GiNails } from 'react-icons/gi';
import './css/Services.css';

const Services = () => {
    const location = useLocation();
    const navigate = useNavigate(); // Sử dụng useNavigate để điều hướng
    const { selectedServices = [], totalPayment = 0 } = location.state || {}; // Nhận state được truyền qua

    const [showTechnician, setShowTechnician] = useState(false); // Trạng thái để hiển thị danh sách nhân viên
    const [selectedTechnician, setSelectedTechnician] = useState(null);

    const handleViewAllServices = () => {
        navigate('/all-services', { state: { selectedServices } }); // Điều hướng đến trang all-services và truyền các dịch vụ đã chọn
    };

    const toggleTechnicianDropdown = () => {
        setShowTechnician(!showTechnician);
    };

    const handleTechnicianSelect = (technician) => {
        setSelectedTechnician(technician);
        setShowTechnician(false);
    };

    const technicians = [
        { id: 1, name: "Wendy", image: '/images/people.png' },
        { id: 2, name: "Trung", image: '/images/people.png' },
        { id: 3, name: "Kelly", image: '/images/people.png' },
        { id: 4, name: "Ken", image: '/images/people.png' },
        { id: 5, name: "Tina", image: '/images/people.png' }
    ];

    return (
        <div className="booking-container">
            <h1 className="booking-header">Booking Appointment</h1>

            {/* Step 1: Select Salon */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">1</div>
                    <h2>Salon</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <FaMapMarkerAlt className="mr-2 text-pink-500" />
                        <span>292 Stone Rd W Unit 7A, Guelph, ON N1G 3C4, Canada</span>
                    </div>
                </div>
            </div>

            {/* Step 2: Choose Service */}
            <div className="step-container" onClick={handleViewAllServices}>
                <div className="step-title">
                    <div className="step-number">2</div>
                    <h2>Choose Service</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center">
                        <GiNails className="mr-2" />
                        {selectedServices.length === 0 ? (
                            <span>View All Services</span>
                        ) : (
                            <span>Selected {selectedServices.length} Services</span>
                        )}
                    </div>
                    <FaChevronRight />
                </div>
            </div>

            {/* Hiển thị các dịch vụ đã chọn */}
            {selectedServices.length > 0 && (
                <div className="selected-services-list">
                    {selectedServices.map((service, index) => (
                        <div key={index} className="selected-service-item">
                            {service.name}
                        </div>
                    ))}
                    <div className="total-payment">
                        Total Payment: ${totalPayment.toFixed(2)}
                    </div>
                </div>
            )}

            {/* Step 3: Choose Date, Time & Stylist */}
            <div className="step-container">
                <div className="step-title">
                    <div className="step-number">3</div>
                    <h2>Choose Date, Time & Stylist</h2>
                </div>
                <div className="step-content">
                    <div className="flex items-center technician-dropdown" onClick={toggleTechnicianDropdown}>
                        <FaMapMarkerAlt className="mr-2" />
                        <span>{selectedTechnician ? `Technician: ${selectedTechnician.name}` : "Nail Technician"}</span>
                        <FaChevronRight className={`ml-auto ${showTechnician ? "rotate-180" : "rotate-90"}`} />
                    </div>
                    {showTechnician && (
                        <div className="technician-carousel">
                            {technicians.map((tech, index) => (
                                <div
                                    key={index}
                                    className={`technician-option ${selectedTechnician?.name === tech.name ? "selected" : ""}`}
                                    onClick={() => handleTechnicianSelect(tech)}
                                >
                                    <img src={tech.image} alt={tech.name} className="technician-image" />
                                    <span>{tech.name}</span>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Confirm Button */}
            <button className="confirm-button">Confirm Booking</button>
            <p className="note-text">You can cancel or reschedule without any fee.</p>
        </div>
    );
};

export default Services;
